export default function(state = languages[BR_LANG], action) {
	let currentLang
	switch(action.type) {
	case LANGUAGE_CHANGED:
		return languages[action.payload]
	case CLEAN_UP_STATES:
		return languages[BR_LANG]
	}
	return state
}